import {Menu} from 'semantic-ui-react';
import {useState} from 'react';


export default function Layout(props) {
    const [name,setName] = useState('home');
    handleItemClick = () => setName({ activeItem: name })

    return (<>
        <header>
            <Menu pointing secondary>
                <Menu.Item  icon="file outline" name='login' href="/"/>
                <Menu.Item  icon="sign in alternate" name='form' href="/about"/>
            </Menu>
        </header>
        {props.children}
        <style jsx>{`
            h1 {
                border-bottom: 1px solid gray;
            }
        `}
        </style>
    </>)
}

