import { Segment,Grid,Image,List,Form,Header } from 'semantic-ui-react';
import {useState, useEffect} from 'react';
import axios from 'axios';

const programOptions = [
    { key: 'o', text: '', value: 'other' },
    { key: 'b', text: 'Boarding', value: 'boarding' },
    { key: 'bt', text: 'Board & Train', value: 'board and train' },    
    { key: 't', text: 'Train', value: 'train' },
    { key: 'g', text: 'Going Home', value: 'gohome' },
];
const heelOptions = [
    { key: 'l', text: 'Left', value: 'left' },
    { key: 'r', text: 'Right', value: 'right' },
];
export default (props) => {
    console.log('profile:' ,props)
    const [data,setData]=useState({
        id: props.data.id,
        heel: props.data.heel,
        program: props.data.program,
        intakeDate: props.data.intakeDate,
    })
    useEffect(()=>{
        axios.get('/api/dogprofile/'+props.data.id)
        .then(res => {
            console.log(res);
        })
        .catch(err => console.log('Error:',err))
    },[])
    const handleInput = (event)=>{
        console.log(event.target.name,event.target.value)

    }
    const handleDropdown = (event,data)=>{
        console.log(data.name,data.value)
        // let newObj = {[data.name]: data.value};
        // setForm({...form,...newObj});
    }
    const handleClick = async (event)=>{
        event.preventDefault();

    }
    return (<Grid divided='vertically'>
            <Grid.Row columns={2}>
                <Grid.Column width={6}>
                    <Image src='https://www.pngkey.com/png/detail/204-2046914_diane-vulcan-shared-rhodesian-ridgeback.png' size='medium' circular />
                </Grid.Column>
                <Grid.Column width={10}>
                    <Header as='h1'>Basic Info</Header>
                    <List>
                        <List.Item>Name: {props.data.dogName}</List.Item>
                        <List.Item>Age: {props.data.dogAge}</List.Item>
                        <List.Item>Breed: {props.data.dogBreed}</List.Item>
                        <List.Item>Gender: {props.data.dogGender}</List.Item>
                        <List.Item>
                            Owner Contact Information: 
                            <p>
                                <span style={{fontWeight:"bold"}}>{'Name: '}</span>
                                {`${props.data.ownerFirstName} ${props.data.ownerLastName}`}
                            </p>
                            <p>
                                <span style={{fontWeight:"bold"}}>{'Phone: '}</span>
                                {props.data.ownerCellPhone}
                            </p>
                            <p>
                                <span style={{fontWeight:"bold"}}>{'Email: '}</span>
                                {props.data.ownerEmail}
                            </p>
                        </List.Item>
                        <List.Item>Food Information: Name: Nature's Logic Sardine flavor Amount: 4 Time: Morning and Evening</List.Item>
                        <List.Item>Fixed: {props.data.fixed}</List.Item>
                        <List.Item>Veterinarian Information: Name: Parmer Lane Pet Hospital Phone: 512-260-5443</List.Item>
                        <List.Item>Allergies: {props.data.allegires}</List.Item>
                        <List.Item>Medication: {props.data.medication}</List.Item>
                    </List>
                </Grid.Column>
            </Grid.Row> 
            <Grid.Row stretched columns={2}>
                <Grid.Column width={8}>
                    <Segment>
                        <Header as='h3' >Issues</Header>
                        <List>
                            <List.Item>None Provided</List.Item>
                        </List>
                    </Segment>
                </Grid.Column>
                <Grid.Column width={8}>
                    <Segment>                        
                        <Header as='h3' >Extra Comments</Header>
                        <p>Extra Issues: She is too stinking cute for her own good</p>
                        <p>Owner Notes: (if blank) None Provided</p>
                    </Segment>
                    <Segment>
                        <Header as='h3' >Known Commands</Header>
                        <p>All of them</p>
                    </Segment>
                </Grid.Column>
            </Grid.Row>

            <Grid.Row stretched columns={1}>
                <Header as='h1' style={{marginLeft:'10px'}}>Intake Info (provided by trainer)</Header>
                <Grid.Column>
                    <Form>
                        <Form.Group widths='equal'>
                            <Form.Select
                                fluid
                                label = 'Heel'
                                options={heelOptions}
                                placeholder = 'Left'
                                value = {data.heel}
                                onChange = {handleDropdown}
                                name= 'heel'
                            />
                            <Form.Select
                                fluid
                                label = 'Program'
                                options={programOptions}
                                placeholder = 'Program'
                                onChange = {handleDropdown}
                                value = {data.program}
                                name= 'program'
                            />
                            <Form.Input 
                                fluid 
                                label='Intake Date' 
                                name='dateofIntake' 
                                type='date' 
                                value = {data.intakeDate}
                                onChange = {handleInput}
                            />
                        </Form.Group>
                        <Form.Button positive>Save</Form.Button>
                    </Form>
                </Grid.Column>
            </Grid.Row>
            <Grid.Row></Grid.Row>
        </Grid>
    );
};
