import { Button, Modal, Icon } from 'semantic-ui-react';
import {useState} from 'react';
export default (props) => {
  const [modalstate,setModalstate] = useState(false);
    const handleModal = ()=> {
        modalstate?setModalstate(false):setModalstate(true);
        console.log(modalstate)
  };
  return (
  <Modal size='small' dimmer= 'blurring' onClose={handleModal} open={modalstate} trigger={
    <Button icon color={props.buttonColor} floated={props.buttonLocation} onClick={props.handleModal} style={{marginBottom:'5px'}}>
        <p><Icon name={props.icon} /></p>
    </Button>
  } closeIcon>
    <Modal.Header>{props.title}</Modal.Header>
    <Modal.Content>
      {props.modalContent}
    </Modal.Content>
    <Modal.Actions>
        {props.modalActions}
    </Modal.Actions>
  </Modal>
)};
