import InternalLayout from '../components/InternalLayout';
import TableTemplate from '../components/TableTemplate';
import ModalLayout from '../components/ModalLayout';
import { Table, Button, Icon } from 'semantic-ui-react';
import {useEffect,useState} from 'react';
import axios from 'axios';


export default (props) => {
    const loadingData =[{
        id:0,
        user:'Loading',
        adminflag:'Loading',
        password:'Loading'
    }]
    const [data,setData] = useState(loadingData);
    ///when first loading pull all the users from database and display them
    //const tableData = data.length===0? loadingData:data
    useEffect(() => {
        axios.get('/api/users')
        .then(res => {
            let thepassword={password: '******'}
            let theadminflag = {adminflag: res.data.adminflag?'y':'n'}
            setData({...res.data,...theadminflag,...thepassword});
        })
        .catch(err => console.log('Error:',err))
    },[]);
    //to edit user info click on name and a pop up will ask you 
    const mymodal = (text,rowdata) => <>
        {text}
        <ModalLayout 
            buttonLocation='right' 
            buttonName='Remove' 
            icon='trash' 
            title='Delete user'
            modalContent={<p>{`Are you sure you want to delete account?`}</p>}
            modalActions={<p>{`OK`}</p>}
            current={rowdata}
        /></>
    let headerData = [{
        tableHeaderName:'user',
        name: 'Username',
        clickFunction: 'handleUserClick',
        haschildren: false,
        children: ''
    },
    {
        tableHeaderName:'password',
        name: 'Password',
        clickFunction: 'handlePasswordClick',
        haschildren: false,
        children: ''
    },
    {
        tableHeaderName:'adminflag',
        name: 'AdminFlag',
        clickFunction: 'handleAdminClick',
        haschildren: true,
        children: mymodal
    }]

    ///need body info
    console.log(data)
    let tableData = [
        { user: 'maria',password: '******', adminflag: 'n' ,id: 2 },
        { user: 'admin',password: '******',adminflag: 'y' ,id: 1 }
    ]
    console.log('inside /dogs',props)
    return <><InternalLayout adminFlag='y'/><TableTemplate header={headerData} table={tableData}></TableTemplate></>
}
