import Layout from '../components/Layout';
import MyGrid from '../components/MyGrid';
import {Form, Button} from 'semantic-ui-react';
import { useState } from 'react';
import axios from 'axios';

export default () => {
  const [login,setLogin] = useState({
    user: '',
    password: ''
  })
  const handleSubmit = (event)=>{
    event.preventDefault();
    console.log('pressed submit')
    console.log(login)
    getUser(login).then((req,res)=>{
      console.log('response returned from get request route /login')
      console.log(res)
    })
    //done after data is sent for authentication
    ///this clears object
    setLogin({
      user: '',
      password: ''
    });

  };
  const handleLoginChange = (event)=>{
    let newObj = {[event.target.name]: event.target.value};
    ///this will not override the data that already exists in obj login
    setLogin({...login,...newObj});
  }
  const getUser = (data)=>{
    axios.get('/login',data)
  }

  return (<Layout>
    <MyGrid 
        columnNum = {2} 
        header='Login'
        message='Not registered yet? Contact your admin at email@example.com to create your account.'
    >
        <Form size="large">
            <Form.Input
                fluid
                icon="user"
                iconPosition="left"
                placeholder="Username"
                onChange={handleLoginChange}
                name='user'
                type='text'
                value={login.user}
            />
            <Form.Input
                fluid
                icon="lock"
                iconPosition="left"
                placeholder="Password"
                type="password"
                onChange={handleLoginChange}
                name='password'
                type='text'
                value={login.password}
            />
            <Button type='submit' size="large" onClick={handleSubmit}>Log In</Button>
        </Form>
    </MyGrid>
  </Layout>
  )};
