import Layout from '../components/Layout';
import MyGrid from '../components/MyGrid';
import {Form, Button} from 'semantic-ui-react';
import { useState } from 'react';

export default () => {
  const [login,setLogin] = useState({
    email: '',
    password: ''
  })
  const handleSubmit = (event)=>{
    event.preventDefault();
    console.log('pressed submit')
    console.log(login)
    //done after data is sent for authentication
    ///this clears object
    setLogin({
      email: '',
      password: ''
    });

  };
  const handleLoginChange = (event)=>{
    let newObj = {[event.target.name]: event.target.value};
    setLogin({...login,...newObj});
  }

  return (<Layout>
    <MyGrid 
        columnNum = {2} 
        header='Login'
        message='Not registered yet? Contact your admin at email@example.com to create your account.'
    >
        <Form size="large">
            <Form.Input
                fluid
                icon="user"
                iconPosition="left"
                placeholder="Email address"
                onChange={handleLoginChange}
                name='email'
                type='text'
                value={login.email}
            />
            <Form.Input
                fluid
                icon="lock"
                iconPosition="left"
                placeholder="Password"
                type="password"
                onChange={handleLoginChange}
                name='password'
                type='text'
                value={login.password}
            />
            <Button type='submit' size="large" onClick={handleSubmit}>Log In</Button>
        </Form>
    </MyGrid>
  </Layout>
  )};
