import InternalLayout from '../components/InternalLayout';
import Table from '../components/Table';
import ModalLayout from '../components/ModalLayout';
import RegisterUser from '../components/RegisterUser';
import { Button } from 'semantic-ui-react';
//import { useRouter } from 'next/router';
import {useState} from 'react';

export default (props) => {
    ///figure out how to send the admin flag from the login page
    //let router = useRouter();
    const [user,setUser] = useState({
        username: '',
        password: '',
        admin:false
    });
    const [modalstate,setModalstate] = useState(false);
    const handleModal = ()=> {
        modalstate?setModalstate(false):setModalstate(true);
    };
    const handleInput = (event)=>{
        let newObj = {[event.target.name]:event.target.value};        
        setUser({...user,...newObj});
    }


    const handleCheckBox = (event,data) => {
        let newObj = {[data.name]: data.checked};
        setUser({...user,...newObj});
    }
    const handleButton = (event)=>{
        event.preventDefault();
        console.log(user);
        setModalstate(false);

    }
    // const handleSubmit = async (event)=>{
    //     event.preventDefault();
    //     let response = await axios.post('/login',login)
    //     //need to send the admin flag to users
    //     //router.push({ pathname: response.data.redirectUrl, query: response.data.query.adminflag })
    //     response.data.success? router.push({ pathname: response.data.redirectUrl}): console.log('incorrect password')
    //     //done after data is sent for authentication
    //     ///this clears object
    //     setLogin({
    //       user: '',
    //       password: ''
    //     });
    // };
    return (
    <InternalLayout adminFlag='y'>
        <ModalLayout buttonColor='green'
            modalstate={modalstate}
            openModal={handleModal}
            buttonLocation='right' 
            buttonName='New User' 
            icon='plus' 
            title='Add a new user'
            modalContent={<RegisterUser title='Add User' user={user} inputchange={handleInput} checkboxchange={handleCheckBox}/>}
            modalActions={<Button positive content='OK' onClick={handleButton} />}
            user={user}
        >
        </ModalLayout>
        <Table ></Table>
    </InternalLayout>);
}
