import InternalLayout from '../components/InternalLayout';
import Table from '../components/Table';
import ModalLayout from '../components/ModalLayout';
import RegisterUser from '../components/RegisterUser';
import { Button, Icon } from 'semantic-ui-react';
import {useEffect,useState} from 'react';
import axios from 'axios';

const ModalAction = () => <Button positive content='OK' />
//the admin flag is based on who signs in at the beginning 
export default () => {
    const [data,setData] = useState([]);
    //onLoad we want to do a get

    // axios.get('/api/users').then((response)=>{
    //     console.log('get request response',response.data)
    // })
    useEffect(()=>{
        // async function fetchData (){
        //     console.log('inside users get request')
        //     try{
        //         return await axios.get('/api/users');
        //     }
        //     catch(e){console.log(e)}
        // }
        // const newData = fetchData();
        // console.log(JSON.stringify(newData))
        // if(newData){
        //     console.log(JSON.stringify(newData))
        //     setData(newData)
        // }
        axios.get('/api/users').then((response)=>{
            console.log('get request response',response.data)
            if(response.data){
                console.log(JSON.stringify(response.data))
                setData(response.data)
            }
        })
    },[])
    return (
    <InternalLayout adminFlag='y'>
        <ModalLayout buttonColor='green'
            buttonLocation='right' 
            buttonName='New User' 
            icon='plus' 
            title='Add a new user'
            modalContent={<RegisterUser title='Add User'/>}
            modalActions={<ModalAction />}
        >
        </ModalLayout>
        <Table tableInfo={data? 'Loading':data}></Table>
    </InternalLayout>);
}
