import InternalLayout from '../components/InternalLayout';
import Table from '../components/Table';
import ModalLayout from '../components/ModalLayout';
import RegisterUser from '../components/RegisterUser';
import { Button } from 'semantic-ui-react';
//import { useRouter } from 'next/router';
import {useState} from 'react';

export default (props) => {
    ///figure out how to send the admin flag from the login page
    //let router = useRouter();
    const [user,setUser] = useState({
        username: '',
        password: '',
        admin:''
    });

    const handleChange = (event)=>{
        console.log('before:',user)
        let newObj = {[event.target.name]:event.target.value};        
        setUser({...user,newObj});
        console.log('after:',user)
    }

    return (
    <InternalLayout adminFlag='y'>
        <ModalLayout buttonColor='green'
            buttonLocation='right' 
            buttonName='New User' 
            icon='plus' 
            title='Add a new user'
            modalContent={<RegisterUser title='Add User' user={user} change={handleChange}/>}
            modalActions={<Button positive content='OK' />}
            user={user}
        >
        </ModalLayout>
        <Table ></Table>
    </InternalLayout>);
}
