import { Button, Modal } from 'semantic-ui-react'

export default (props) => (
  <Modal size='small' dimmer= 'blurring' trigger={<Button>{props.button}</Button>} closeIcon>
    <Modal.Header>{props.title}</Modal.Header>
    <Modal.Content>
      {props.children}
    </Modal.Content>
  </Modal>
)
