import InternalLayout from '../components/InternalLayout';
import TableTemplate from '../components/TableTemplate';
import ModalLayout from '../components/ModalLayout';
import {useEffect,useState} from 'react';
import axios from 'axios';
import { Table, Button, Icon } from 'semantic-ui-react';

const ModalAction = (props) => <Button positive content='OK' onClick={props.handleButton}/>
/*
<ModalLayout 
buttonLocation='right' 
buttonName='Remove' 
icon='trash' 
title='Delete user'
modalContent={<ModalContent text={`Are you sure you want to delete ${user} account?`}/>}
modalActions={<ModalAction />}
current={{user:user,adminflag:adminflag,id:id,password:''}}
>
</ModalLayout>
*/

export default (props) => {
    const loadingData =[{
        id:0,
        user:'Loading',
        adminflag:'Loading',
        password:'Loading'
    }]
    const [data,setData] = useState(loadingData);
    ///when first loading pull all the users from database and display them
    //const tableData = data.length===0? loadingData:data
    useEffect(() => {
        axios.get('/api/users')
        .then(res => {
            setData(res.data.map(row=>({ user: row.user,password: '******', adminflag: row.adminflag?'y':'n' ,id: row.id })));
        })
        .catch(err => console.log('Error:',err))
    },[]);
    //to edit user info click on name and a pop up will ask you 
    const mymodal = (text,rowdata) => <>
        {text}
        <ModalLayout 
            buttonLocation='right' 
            buttonName='Remove' 
            icon='trash' 
            title='Delete user'
            modalContent={<p>{`Are you sure you want to delete ${rowdata.user} account?`}</p>}
            modalActions={<ModalAction />}
            current={rowdata}
        /></>
    let headerData = [{
        tableHeaderName:'user',
        name: 'Username',
        clickFunction: 'handleUserClick',
        haschildren: false,
        children: ''
    },
    {
        tableHeaderName:'password',
        name: 'Password',
        clickFunction: 'handlePasswordClick',
        haschildren: false,
        children: ''
    },
    {
        tableHeaderName:'adminflag',
        name: 'AdminFlag',
        clickFunction: 'handleAdminClick',
        haschildren: true,
        children: mymodal
    }]
    console.log('inside /dogs',props)
    return <>
        <InternalLayout adminFlag='y'>
            <TableTemplate header={headerData} table={data}>
            </TableTemplate>
        </InternalLayout>
        </>
}
