    }

    return (
    <InternalLayout adminFlag='y'>
        <ModalLayout buttonColor='green'
            buttonLocation='right' 
            buttonName='New User' 
            icon='plus' 
            title='Add a new user'
            modalContent={<RegisterUser title='Add User' inputchange={handleChange} checkboxchange={handleCheckBox}/>}
            modalActions={<Button positive content='OK' />}
            user={user}
        >
        </ModalLayout>
        <Table ></Table>
    </InternalLayout>);
}
