import { Button, Modal, Icon } from 'semantic-ui-react';
import {useState, useEffect} from 'react';
import RegisterUser from '../components/RegisterUser';

export default (props) => {
  const [user,setUser] = useState(props.current?props.current:{
    username: '',
    admin:false,
    id:0,
    password: '',

  });
  const [modalstate,setModalstate] = useState(false);
  const handleModal = (event)=> {
    modalstate?setModalstate(false):setModalstate(true);
  };
  const handleInput = (event)=>{
    let newObj = {[event.target.name]:event.target.value};        
    setUser({...user,...newObj});
  };  
  const handleCheckBox = (event,data) => {
    let newObj = {[data.name]: data.checked};
    setUser({...user,...newObj});
  };
  const handleButton = async (event)=>{
    event.preventDefault();
    console.log(user);
    setModalstate(false);
    let response = () => {switch (props.buttonName) {
      case 'Remove':
        console.log('delete button')
        break;
      case 'New User' :
          console.log('delete button')
          break;

      default:
        break;
    }}
    //await axios
  };
// const handleSubmit = async (event)=>{
//     event.preventDefault();
//     let response = await axios.post('/login',login)
//     //need to send the admin flag to users
//     //router.push({ pathname: response.data.redirectUrl, query: response.data.query.adminflag })
//     response.data.success? router.push({ pathname: response.data.redirectUrl}): console.log('incorrect password')
//     //done after data is sent for authentication
//     ///this clears object
//     setLogin({
//       user: '',
//       password: ''
//     });
// };
  return (
  <Modal size='small' dimmer= 'blurring' onClose={handleModal} open={modalstate} trigger={
    <Button icon color={props.buttonColor} floated={props.buttonLocation} onClick={handleModal} style={{marginBottom:'5px'}} data={props.test}>
        <p><Icon name={props.icon} /></p>
    </Button>
  } closeIcon>
    <Modal.Header>{props.title}</Modal.Header>
    <Modal.Content>
      {props.modalContent? props.modalContent: <RegisterUser title={props.buttonName} value={user} inputchange={handleInput} checkboxchange={handleCheckBox}/>}
    </Modal.Content>
    <Modal.Actions>
      <Button positive content='OK' onClick={handleButton} />
    </Modal.Actions>
  </Modal>
)};
