import Layout from '../components/Layout';
import MyGrid from '../components/MyGrid';
import {Form, Button} from 'semantic-ui-react';
import { useState } from 'react';

export default () => {
  const [login,setLogin] = useState({
    email: '',
    password: ''
  })
  const handleSubmit = (event)=>{
    event.preventDefault();
    console.log('pressed submit')

    console.log(login)
  };
  const handleLoginChange = (event)=>{
    console.log('login data changed')
    console.log(event.target.name)
    console.log(event.target.value)
    let newObj = {[event.target.name]: event.target.value};
    setLogin({...login,...newObj});
  }

  return (<Layout>
    <MyGrid 
        columnNum = {2} 
        header='Login'
        message='Not registered yet? Contact your admin at email@example.com to create your account.'
    >
        <Form size="large">
            <Form.Input
                fluid
                icon="user"
                iconPosition="left"
                placeholder="Email address"
                onChange={handleLoginChange}
                name='email'
            />
            <Form.Input
                fluid
                icon="lock"
                iconPosition="left"
                placeholder="Password"
                type="password"
                onChange={handleLoginChange}
                name='password'
            />
            <Button type='submit' size="large" onClick={handleSubmit}>Log In</Button>
        </Form>
    </MyGrid>
  </Layout>
  )};
